/**
 * Determines if any elements in the given array satisfy the provided testing function.
 * If no testing function is provided, it checks if the array has any elements.
 *
 * @template T - The type of elements in the array.
 *
 * @param arr - The array or undefined to be checked.
 * @param fn - An optional testing function. If provided, will be used to test each element in the array.
 *
 * @returns `true` if the array is defined and has at least one element that satisfies the testing function (or just one element if no function is provided), `false` otherwise.
 *
 * @example
 * // With testing function
 * any([1, 2, 3, 4], x => x > 2); // Returns true
 *
 * // Without testing function
 * any([1, 2, 3, 4]); // Returns true
 * any([]); // Returns false
 */
export declare function any<T>(arr: T[] | undefined, fn?: (x: T) => boolean): arr is T[];
